flow.chain:
THEN(
/** 判断是否登录并将 session_uid 映射为 UserInfo_Id，供管理员校验使用 **/
UserLoginGuard,
/** 查询频道信息 **/
CPChannelSelector.bind("key","id"),
/** 校验当前用户是否为频道管理员 **/
CPChannelAdminChecker,
/** 查询被封禁目标成员信息 **/
CPChannelMemberSelector.bind("key","CidWithUid"),
/** 检查封禁目标是否合法（不可封禁管理员） **/
CPChannelBanTargetChecker,
/** 保存封禁记录 **/
CPChannelBanSaver,
/** 收集频道所有成员用于通知 **/
CPChannelMemberCollector,
/** 通知频道内所有成员 **/
CPNotifier.bind("route","/core/channel/ban/list")
)

THEN(
/** 判断是否登录并将 session_uid 映射为 UserInfo_Id，供管理员校验使用 **/
UserLoginGuard,
/** 查询频道信息 **/
CPChannelSelector.bind("key","id"),
/** 校验当前用户是否为频道管理员 **/
CPChannelAdminChecker,
/** 查询封禁记录 **/
CPChannelBanSelector,
/** 删除封禁记录 **/
CPChannelBanDeleter,
/** 收集频道所有成员用于通知 **/
CPChannelMemberCollector,
/** 通知频道内所有成员 **/
CPNotifier.bind("route","/core/channel/ban/list")
)

THEN(
/** 判断是否登录并将 session_uid 映射为 UserInfo_Id **/
UserLoginGuard,
/** 参数重命名 + 成员存在校验 **/
MemberGuard,
/** 拉取封禁列表 **/
CPChannelBanLister
)
